import React, { useState, useEffect } from 'react';
import { Container, Paper, TextField, Button, Box, Typography, List, ListItem, ListItemText, Divider, Alert, Snackbar } from '@mui/material';
import io from 'socket.io-client';

const socket = io('http://localhost:5001', {
  reconnection: true,
  reconnectionAttempts: 5,
  reconnectionDelay: 1000,
});

function App() {
  const [username, setUsername] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const [isJoined, setIsJoined] = useState(false);
  const [error, setError] = useState('');
  const [openSnackbar, setOpenSnackbar] = useState(false);

  useEffect(() => {
    socket.on('message', (message) => {
      setMessages((msgs) => [...msgs, message]);
    });

    socket.on('userList', (userList) => {
      setUsers(userList);
    });

    socket.on('joinSuccess', () => {
      setIsJoined(true);
      setError('');
    });

    socket.on('error', (errorMessage) => {
      setError(errorMessage);
      setOpenSnackbar(true);
      if (errorMessage === 'Username already taken') {
        setIsJoined(false);
      }
    });

    socket.on('connect_error', () => {
      setError('Connection error. Please try again.');
      setOpenSnackbar(true);
      setIsJoined(false);
    });

    socket.on('disconnect', () => {
      setError('Disconnected from server. Trying to reconnect...');
      setOpenSnackbar(true);
      setIsJoined(false);
    });

    return () => {
      socket.off('message');
      socket.off('userList');
      socket.off('joinSuccess');
      socket.off('error');
      socket.off('connect_error');
      socket.off('disconnect');
    };
  }, []);

  const handleJoin = (e) => {
    e.preventDefault();
    if (username.trim()) {
      setError('');
      socket.emit('join', username.trim());
    }
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (message.trim()) {
      socket.emit('message', message.trim());
      setMessage('');
    }
  };

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  if (!isJoined) {
    return (
      <Container maxWidth="sm">
        <Box sx={{ mt: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Paper elevation={3} sx={{ p: 4, width: '100%' }}>
            <Typography variant="h5" component="h1" gutterBottom>
              Join Chat
            </Typography>
            <form onSubmit={handleJoin}>
              <TextField
                fullWidth
                label="Enter your username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                margin="normal"
                error={!!error}
                helperText={error}
                disabled={socket.disconnected}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 2 }}
                disabled={!username.trim() || socket.disconnected}
              >
                Join
              </Button>
            </form>
          </Paper>
        </Box>
        <Snackbar
          open={openSnackbar}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>
      </Container>
    );
  }

  return (
    <Container maxWidth="md">
      <Box sx={{ mt: 4, display: 'flex', gap: 2 }}>
        <Box sx={{ flex: 1 }}>
          <Paper elevation={3} sx={{ p: 2, height: '70vh', display: 'flex', flexDirection: 'column' }}>
            <Typography variant="h6" gutterBottom>
              Chat Room (Logged in as {username})
            </Typography>
            <List sx={{ flex: 1, overflow: 'auto', mb: 2 }}>
              {messages.map((msg, index) => (
                <React.Fragment key={index}>
                  <ListItem>
                    <ListItemText
                      primary={msg.text}
                      secondary={`${msg.user} - ${new Date(msg.timestamp).toLocaleTimeString()}`}
                    />
                  </ListItem>
                  <Divider />
                </React.Fragment>
              ))}
            </List>
            <form onSubmit={handleSendMessage} style={{ display: 'flex', gap: 8 }}>
              <TextField
                fullWidth
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Type a message"
                variant="outlined"
                size="small"
                disabled={socket.disconnected}
              />
              <Button 
                type="submit" 
                variant="contained"
                disabled={!message.trim() || socket.disconnected}
              >
                Send
              </Button>
            </form>
          </Paper>
        </Box>
        <Paper elevation={3} sx={{ p: 2, width: 200 }}>
          <Typography variant="h6" gutterBottom>
            Online Users
          </Typography>
          <List>
            {users.map((user, index) => (
              <ListItem key={index}>
                <ListItemText primary={user} />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Box>
      <Snackbar
        open={openSnackbar}
        autoHideDuration={6000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default App;
